            List<int> input = FragileHelper.ListFromString(s);
            List<string> result = new List<string>();

            int approxdist = 0;
            for (int i = 0; i < input.Count(); i++)
            {
                if (Math.Abs(input[i]) != i + 1)
                {
                    // apply the k-sorting reversal to P
                    input = FragileHelper.KSortingReversal(input, i);
                    result.Add(FragileHelper.StringFromList(input));
                    approxdist++;
                }
                if (input[i] != Math.Abs(input[i]))
                {
                    // apply the reversal flipping the k-th element of P
                    input = FragileHelper.ReversalFlipping(input, i);
                    result.Add(FragileHelper.StringFromList(input));
                    approxdist++;
                }
            }

            //GREEDYSORTING(P)
            //approxReversalDistance ← 0
            //for k = 1 to |P|
            //    if element k is not sorted
            //        apply the k-sorting reversal to P
            //        approxReversalDistance ← approxReversalDistance + 1
            //    if k-th element of P is −k
            //        apply the reversal flipping the k-th element of P
            //        approxReversalDistance ← approxReversalDistance + 1
            //return approxReversalDistance

            Helper.WriteStringsToTextFile(result);
            return result;
        }

